package com.obj;

class Alpha {
	int x;
}

public class ObjDemo {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Alpha a1 = new Alpha();
		a1.x = 4;
		System.out.println(a1.x);
		Alpha a2 = new Alpha();
		a2.x = 7;
		System.out.println(a2.x);

	}

}
