package com.ge.crd.ementoring.util;

public class MenteeQuery {
	
	public static final String APP_MENTEE_EDIT_QUERY = "SELECT A.MENTEE_BAND, SECURE_DATA.DECRYPTDATA(B.BAND_DESCRIPTION) BAND_DESCRIPTION,A.ELEMENTS_DESIRED_IN_MEN_REL, A.DESIRED_FRE_OF_MEET_WITH_MENT, A.AREA_OF_INTEREST, A.PERSONAL_DETAILS FROM MENTOR_REGISTERED_MENTEES A, MENTOR_BAND_MASTER B WHERE A.MENTEE_BAND = B.BAND_ID AND SECURE_DATA.DECRYPTDATA(MENTEE_SSOID) = ?";
	public static final String APP_MENTEE_SAVE_LIST_QUERY = "SELECT A.MENTEE_BAND,A.ELEMENTS_DESIRED_IN_MEN_REL, A.DESIRED_FRE_OF_MEET_WITH_MENT,A.AREA_OF_INTEREST, A.PERSONAL_DETAILS FROM MENTOR_REGISTERED_MENTEES A WHERE SECURE_DATA.DECRYPTDATA(MENTEE_SSOID) = ? ";
	public static final String APP_MENTEE_UPDATE_QUERY = "UPDATE MENTOR_REGISTERED_MENTEES SET REGISTRATION_DATE = TO_DATE(SYSDATE,'DD-MON-YY'), MENTEE_BAND=?, ELEMENTS_DESIRED_IN_MEN_REL = ?, DESIRED_FRE_OF_MEET_WITH_MENT = ?, AREA_OF_INTEREST = ?, PERSONAL_DETAILS = ?, UPDATE_PROFILE_DATE = TO_DATE(sysdate,'DD-MON-YY'), STATUS_ID = ? WHERE SECURE_DATA.DECRYPTDATA(MENTEE_SSOID) = ?";
	public static final String APP_MENTEE_UPDATE_REGISTER_MENTEE_QUERY = "UPDATE MENTOR_REGISTERED_MENTEES SET ELEMENTS_DESIRED_IN_MEN_REL = ?, DESIRED_FRE_OF_MEET_WITH_MENT = ?, AREA_OF_INTEREST = ?, PERSONAL_DETAILS = ?, UPDATE_PROFILE_DATE = TO_DATE(sysdate,'DD-MON-YY') WHERE SECURE_DATA.DECRYPTDATA(MENTEE_SSOID) = ?"; 
	public static final String APP_MENTOR_MENTEE_BUSINESSUNIT_LIST_QUERY = "SELECT DISTINCT BUSINESS_NAME FROM MENTOR_SPODA_BUSINESS_MAPPING WHERE LOCATION_ID = ? ORDER BY BUSINESS_NAME" ;
	public static final String APP_MENTOR_MENTEE_PATH_LIST_QUERY="SELECT PATH_ID, PATH_DESCRIPTION FROM MENTOR_MENTORING_PATH_MASTER ORDER BY PATH_ID";
	
    public static final String APP_MENTOR_DETAILS_LIST_QUERY = "SELECT A.GEHRFULLNAME, A.GEBUSINESSUNIT, A.GEHRPOSITIONTITLE,A.MAIL,A.GEHRDAILCOM,B.STRENGHS_COMPETENCIES,B.PERSONAL_DETAILS,B.AREA_OF_INTEREST,C.STATUS_DESCRIPTION, TO_CHAR(D.MENTEE_INITIATED_DATE,'DD-MON-YY') MENTEE_INITIATED_DATE ,TO_CHAR(D.APPROVAL_REJECTION_DATE,'DD-MON-YY') APPROVAL_REJECTION_DATE,E.PATH_DESCRIPTION FROM MENTOR_SPODA_EMPLOYEE A, MENTOR_REGISTERED_MENTORS B,MENTOR_STATUS_MASTER C,MENTOR_MENTOR_MENTEE_MAPPING D,MENTOR_MENTORING_PATH_MASTER E WHERE SECURE_DATA.DECRYPTDATA(D.MENTEE_SSOID)= ? AND SECURE_DATA.DECRYPTDATA(D.MENTOR_SSOID)=? AND D.STATUS_ID=? AND E.PATH_ID = B.PATH_ID AND C.STATUS_ID = D.STATUS_ID AND A.SSOID = SECURE_DATA.DECRYPTDATA(B.MENTOR_SSOID) AND SECURE_DATA.DECRYPTDATA(B.MENTOR_SSOID)=?";
    public static final String APP_MENTOR_DETAILS_LIST_QUERY1 = "SELECT A.GEHRFULLNAME, A.GEBUSINESSUNIT, A.GEHRPOSITIONTITLE,A.MAIL,A.GEHRDAILCOM,B.MENTOR_REGISTERED_SLNO,B.STRENGHS_COMPETENCIES,B.PERSONAL_DETAILS, B.AREA_OF_INTEREST,C.PATH_DESCRIPTION FROM MENTOR_SPODA_EMPLOYEE A,MENTOR_REGISTERED_MENTORS B,MENTOR_MENTORING_PATH_MASTER C WHERE A.SSOID = SECURE_DATA.DECRYPTDATA(B.MENTOR_SSOID) AND C.PATH_ID = B.PATH_ID AND SECURE_DATA.DECRYPTDATA(B.MENTOR_SSOID) =?";
    
    public static final String APP_MENTEE_DETAILS_LIST_QUERY = "SELECT A.GEHRFULLNAME, A.GEBUSINESSUNIT, A.GEHRPOSITIONTITLE ,B.MENTEE_REGISTERED_SLNO,B.ELEMENTS_DESIRED_IN_MEN_REL,B.DESIRED_FRE_OF_MEET_WITH_MENT, B.PERSONAL_DETAILS, B.AREA_OF_INTEREST,C.STATUS_DESCRIPTION, TO_CHAR(D.MENTEE_INITIATED_DATE,'DD-MON-YY') MENTEE_INITIATED_DATE ,TO_CHAR(D.APPROVAL_REJECTION_DATE,'DD-MON-YY') APPROVAL_REJECTION_DATE FROM MENTOR_REGISTERED_MENTEES B, MENTOR_SPODA_EMPLOYEE A ,MENTOR_STATUS_MASTER C,MENTOR_MENTOR_MENTEE_MAPPING D WHERE D.MENTOR_MENTEE_MAPPING_ID=? AND A.SSOID = SECURE_DATA.DECRYPTDATA(B.MENTEE_SSOID) AND SECURE_DATA.DECRYPTDATA(B.MENTEE_SSOID) =? AND C.STATUS_DESCRIPTION=(SELECT STATUS_DESCRIPTION FROM MENTOR_STATUS_MASTER WHERE STATUS_ID=(SELECT STATUS_ID FROM MENTOR_MENTOR_MENTEE_MAPPING WHERE MENTOR_MENTEE_MAPPING_ID=?))";
    public static final String APP_MENTEE_DETAILS_LIST = "SELECT A.GEHRFULLNAME, A.GEBUSINESSUNIT, A.GEHRPOSITIONTITLE ,B.MENTEE_REGISTERED_SLNO,B.ELEMENTS_DESIRED_IN_MEN_REL,B.DESIRED_FRE_OF_MEET_WITH_MENT, B.PERSONAL_DETAILS, B.AREA_OF_INTEREST FROM MENTOR_SPODA_EMPLOYEE A ,MENTOR_REGISTERED_MENTEES B WHERE A.SSOID = SECURE_DATA.DECRYPTDATA(B.MENTEE_SSOID)AND SECURE_DATA.DECRYPTDATA(B.MENTEE_SSOID)=?";
    
    public static final String APP_MENTOR_DETAILS_LIST_BY_MAPPING_ID = "SELECT A.GEHRFULLNAME, A.GEBUSINESSUNIT, A.GEHRPOSITIONTITLE ,A.MAIL,A.GEHRDAILCOM,B.MENTOR_REGISTERED_SLNO,B.STRENGHS_COMPETENCIES,B.PERSONAL_DETAILS,B.AREA_OF_INTEREST,C.STATUS_DESCRIPTION,TO_CHAR(D.MENTEE_INITIATED_DATE,'DD-MON-YY') MENTEE_INITIATED_DATE ,TO_CHAR(D.APPROVAL_REJECTION_DATE,'DD-MON-YY') APPROVAL_REJECTION_DATE,D.COMMENTS,D.STATUS_ID,E.PATH_DESCRIPTION FROM MENTOR_REGISTERED_MENTORS B, MENTOR_SPODA_EMPLOYEE A , MENTOR_STATUS_MASTER C,MENTOR_MENTOR_MENTEE_MAPPING D,MENTOR_MENTORING_PATH_MASTER E WHERE  A.SSOID = SECURE_DATA.DECRYPTDATA(B.MENTOR_SSOID) AND C.STATUS_ID = D.STATUS_ID AND E.PATH_ID = B.PATH_ID AND SECURE_DATA.DECRYPTDATA(B.MENTOR_SSOID)=? AND D.MENTOR_MENTEE_MAPPING_ID = ? ";
    public static final String APP_MENTOR_DETAILS_LIST_QUERY_BY_SSOID = "SELECT A.GEHRFULLNAME, A.GEBUSINESSUNIT, A.GEHRPOSITIONTITLE ,B.STRENGHS_COMPETENCIES,B.PERSONAL_DETAILS, B.AREA_OF_INTEREST,C.STATUS_DESCRIPTION,TO_CHAR(D.MENTEE_INITIATED_DATE,'DD-MON-YY') MENTEE_INITIATED_DATE, TO_CHAR(D.APPROVAL_REJECTION_DATE,'DD-MON-YY') APPROVAL_REJECTION_DATE FROM MENTOR_REGISTERED_MENTORS B, MENTOR_SPODA_EMPLOYEE A ,MENTOR_STATUS_MASTER C,MENTOR_MENTOR_MENTEE_MAPPING D WHERE D.MENTOR_MENTEE_MAPPING_ID=? AND A.SSOID = SECURE_DATA.DECRYPTDATA(B.MENTOR_SSOID) AND SECURE_DATA.DECRYPTDATA(B.MENTOR_SSOID) =? AND C.STATUS_DESCRIPTION=(SELECT STATUS_DESCRIPTION FROM MENTOR_STATUS_MASTER WHERE STATUS_ID=(SELECT STATUS_ID FROM MENTOR_MENTOR_MENTEE_MAPPING WHERE MENTOR_MENTEE_MAPPING_ID=?))";
    
    public static final String APP_MENOR_MENTEE_MAPPING_QUERY = "INSERT INTO MENTOR_MENTOR_MENTEE_MAPPING(MENTOR_MENTEE_MAPPING_ID,MENTEE_SSOID,MENTOR_SSOID,STATUS_ID) VALUES(MENTOR_MAPPING_ID_SEQ.NEXTVAL,secure_data.ENCRYPTDATA(?),secure_data.ENCRYPTDATA(?),?)";
    public static final String APP_MENOR_MENTEE_MAPPING_EXISTANCE_QUERY=" SELECT  B.STATUS_DESCRIPTION FROM MENTOR_MENTOR_MENTEE_MAPPING A, MENTOR_STATUS_MASTER B WHERE SECURE_DATA.DECRYPTDATA(A.MENTEE_SSOID) =? AND SECURE_DATA.DECRYPTDATA(A.MENTOR_SSOID) = ? AND B.STATUS_ID = A.STATUS_ID AND B.STATUS_ID NOT IN(6,8)";    
    public static final String APP_MENTOR_EXISTANCE_MENTEES_QUERY = "SELECT COUNT(MENTEE_SSOID) MENTEES FROM MENTOR_MENTOR_MENTEE_MAPPING WHERE SECURE_DATA.DECRYPTDATA(MENTOR_SSOID)=? AND STATUS_ID = 5";
    public static final String APP_MENTEE_SELECTED_MENTOR_QUERY = "SELECT SECURE_DATA.DECRYPTDATA(MENTOR_SSOID) FROM MENTOR_MENTOR_MENTEE_MAPPING WHERE SECURE_DATA.DECRYPTDATA(MENTEE_SSOID)=? AND STATUS_ID=5" ;
    
    public static final String APP_MENTEE_LIST_QUERY = "SELECT A.SSOID, A.GEHRFULLNAME FROM MENTOR_SPODA_EMPLOYEE A,MENTOR_REGISTERED_MENTEES B WHERE A.SSOID= SECURE_DATA.DECRYPTDATA(B.MENTEE_SSOID) AND B.STATUS_ID = 10" ;
    public static final String APP_MENTOR_LIST_QUERY = "SELECT A.SSOID, A.GEHRFULLNAME FROM MENTOR_SPODA_EMPLOYEE A, MENTOR_REGISTERED_MENTORS B WHERE A.SSOID = SECURE_DATA.DECRYPTDATA(B.MENTOR_SSOID) AND B.STATUS_ID = 2" ;    
    public static final String APP_MENTEE_MENTOR_RELATION_QUERY = "SELECT SECURE_DATA.DECRYPTDATA(MENTEE_SSOID), SECURE_DATA.DECRYPTDATA(MENTOR_SSOID) FROM MENTOR_MENTOR_MENTEE_MAPPING WHERE STATUS_ID =5 "; // IN (4,5)" ;
    
    
    public static final String APP_INPUT_PROGRESS_SAVEDATA_QUERY="INSERT INTO MENTOR_RELATIONSHIP_PROGRESS(MAPPING_ID,INPUT_PERSON_TYPE, PROGRESS_INPUT_BY,PROGRESS_INPUT_DATE,PROGRESS_NOTES,PROGRESS_ID) VALUES(?,?,secure_data.ENCRYPTDATA(?),TO_DATE(?,'DD-MM-YY'),?,MENTOR_PROGRESS_ID_SEQ.NEXTVAL)";
    public static final String APP_MENTEE_MENTOR_MAPPING_ID_QUERY = "SELECT MENTOR_MENTEE_MAPPING_ID FROM MENTOR_MENTOR_MENTEE_MAPPING WHERE SECURE_DATA.DECRYPTDATA(MENTEE_SSOID) = ? AND SECURE_DATA.DECRYPTDATA(MENTOR_SSOID) = ?";
    public static final String APP_INPUT_PROGRESS_DATA_QUERY = "SELECT A.MAPPING_ID,TO_CHAR(A.PROGRESS_INPUT_INITIATED_DATE,'DD-MON-YY') PROGRESS_INPUT_INITIATED_DATE ,TO_CHAR(A.PROGRESS_INPUT_DATE,'DD-MON-YY') PROGRESS_INPUT_DATE,A.PROGRESS_NOTES,B.GEHRFULLNAME,SECURE_DATA.DECRYPTDATA(C.MENTOR_SSOID),SECURE_DATA.DECRYPTDATA(C.MENTEE_SSOID),A.PROGRESS_ID FROM MENTOR_RELATIONSHIP_PROGRESS A,MENTOR_SPODA_EMPLOYEE B,MENTOR_MENTOR_MENTEE_MAPPING C WHERE B.SSOID=SECURE_DATA.DECRYPTDATA(A.PROGRESS_INPUT_BY) AND C.MENTOR_MENTEE_MAPPING_ID = A.MAPPING_ID AND SECURE_DATA.DECRYPTDATA(C.MENTEE_SSOID) = ? OR B.SSOID=SECURE_DATA.DECRYPTDATA(A.PROGRESS_INPUT_BY) AND C.MENTOR_MENTEE_MAPPING_ID = A.MAPPING_ID AND SECURE_DATA.DECRYPTDATA(C.MENTOR_SSOID)=?";
    public static final String APP_INPUT_PROGRESS_DATA_ADMIN_QUERY = "SELECT A.MAPPING_ID,TO_CHAR(A.PROGRESS_INPUT_INITIATED_DATE,'DD-MON-YY') PROGRESS_INPUT_INITIATED_DATE ,TO_CHAR(A.PROGRESS_INPUT_DATE,'DD-MON-YY') PROGRESS_INPUT_DATE,A.PROGRESS_NOTES,B.GEHRFULLNAME,SECURE_DATA.DECRYPTDATA(C.MENTOR_SSOID),SECURE_DATA.DECRYPTDATA(C.MENTEE_SSOID),A.PROGRESS_ID FROM MENTOR_RELATIONSHIP_PROGRESS A,MENTOR_SPODA_EMPLOYEE B,MENTOR_MENTOR_MENTEE_MAPPING C WHERE B.SSOID=SECURE_DATA.DECRYPTDATA(A.PROGRESS_INPUT_BY) AND C.MENTOR_MENTEE_MAPPING_ID = A.MAPPING_ID";
    public static final String APP_INPUT_PROGRESS_DATA_BY_ID_DATE_QUERY = "SELECT A.MAPPING_ID,TO_CHAR(A.PROGRESS_INPUT_INITIATED_DATE,'DD-MON-YY') INPUT_DATE ,TO_CHAR(A.PROGRESS_INPUT_DATE,'DD-MON-YY') PROGRESS_DATE,A.PROGRESS_NOTES,A.PROGRESS_ID,B.GEHRFULLNAME,SECURE_DATA.DECRYPTDATA(C.MENTOR_SSOID),SECURE_DATA.DECRYPTDATA(C.MENTEE_SSOID) FROM MENTOR_RELATIONSHIP_PROGRESS A,MENTOR_SPODA_EMPLOYEE B,MENTOR_MENTOR_MENTEE_MAPPING C WHERE B.SSOID=SECURE_DATA.DECRYPTDATA(A.PROGRESS_INPUT_BY) AND C.MENTOR_MENTEE_MAPPING_ID = A.MAPPING_ID AND A.MAPPING_ID = ? AND A.PROGRESS_ID=?";
    public static final String APP_INPUT_PROGRESS_EDIT_QUERY = "UPDATE MENTOR_RELATIONSHIP_PROGRESS SET PROGRESS_INPUT_BY=secure_data.ENCRYPTDATA(?),PROGRESS_INPUT_DATE=TO_DATE(?,'DD-MM-YY'),PROGRESS_NOTES=? WHERE PROGRESS_ID=? ";
    public static final String APP_NAME_OF_THE_EMPLOYEE_QUERY = "SELECT GEHRFULLNAME FROM MENTOR_SPODA_EMPLOYEE WHERE SSOID=?";
    
    public static final String APP_CURRENT_MENTOR_LIST_QUERY = "SELECT A.SSOID, A.GEHRFULLNAME,A.GEBUSINESSUNIT,A.GEHRPOSITIONTITLE,B.EDUCATIONAL_BACKGROUND,B.POSITIONS_HELD_IN_OUTSIDE_GE,B.STRENGHS_COMPETENCIES,B.PERSONAL_DETAILS,B.AREA_OF_INTEREST,C.PATH_DESCRIPTION FROM MENTOR_SPODA_EMPLOYEE A, MENTOR_REGISTERED_MENTORS B,MENTOR_MENTORING_PATH_MASTER C WHERE C.PATH_ID = B.PATH_ID AND A.SSOID IN(SELECT MENTOR_SSOID FROM MENTOR_MENTOR_MENTEE_MAPPING WHERE MENTEE_SSOID = ? AND STATUS_ID = 5) AND B.MENTOR_SSOID IN(SELECT MENTOR_SSOID FROM MENTOR_MENTOR_MENTEE_MAPPING WHERE MENTEE_SSOID = ? AND STATUS_ID = 5)AND A.SSOID = SECURE_DATA.DECRYPTDATA(B.MENTOR_SSOID)" ;
    public static final String APP_MENTOR_BANDS_LIST_QUERY = "SELECT MENTOR_BANDS FROM MENTOR_MENTEE_MENTOR_BAND_MAP WHERE MENTEE_BAND = (SELECT MENTEE_BAND FROM MENTOR_REGISTERED_MENTEES WHERE SECURE_DATA.DECRYPTDATA(MENTEE_SSOID) = ?)";
    public static final String APP_MENTOR_LOCATION_LIST_QUERY = "SELECT MENTOR_FROM_LOCATION_ID FROM MENTOR_MENTEE_MENTOR_BAND_MAP WHERE LOCATIONID=(SELECT LOCATIONID FROM MENTOR_SPODA_EMPLOYEE WHERE SSOID=?) AND MENTEE_BAND=(SELECT MENTEE_BAND FROM MENTOR_REGISTERED_MENTEES WHERE SECURE_DATA.DECRYPTDATA(MENTEE_SSOID)=?)";

    public static final String APP_MENTOR_REQUIRED_MENTEES_QUERY = "SELECT NO_OF_MENTEES_REQUIRED FROM MENTOR_REGISTERED_MENTORS WHERE SECURE_DATA.DECRYPTDATA(MENTOR_SSOID)=?";
    public static final String APP_MENTEEHRMANAGERID_LIST_QUERY = "SELECT A.SSOID, A.GEHRMANAGERID FROM MENTOR_SPODA_EMPLOYEE A WHERE A.SSOID IN(SELECT SECURE_DATA.DECRYPTDATA(B.MENTEE_SSOID) FROM MENTOR_REGISTERED_MENTEES B )  AND A.GEHRMANAGERID=?";
    public static final String APP_MENTORHRMANAGERID_LIST_QUERY = "SELECT A.SSOID, A.GEHRMANAGERID FROM MENTOR_SPODA_EMPLOYEE A WHERE A.SSOID IN(SELECT SECURE_DATA.DECRYPTDATA(B.MENTOR_SSOID) FROM MENTOR_REGISTERED_MENTORS B )  AND A.GEHRMANAGERID=?";   
    
    public static final String APP_STATUSDESCRIPTION_LIST_QUERY = "SELECT STATUS_ID,STATUS_DESCRIPTION FROM MENTOR_STATUS_MASTER WHERE STATUS_ID IN(4,5,6,7,8,9) ORDER BY STATUS_ID ";
    public static final String APP_STATUS_ID_QUERY = "SELECT MENTOR_MENTEE_MAPPING_ID,STATUS_ID FROM MENTOR_MENTOR_MENTEE_MAPPING WHERE SECURE_DATA.DECRYPTDATA(MENTEE_SSOID) =? AND SECURE_DATA.DECRYPTDATA(MENTOR_SSOID)=?";
    public static final String APP_STATUS_DESC_QUERY="SELECT STATUS_DESCRIPTION FROM MENTOR_STATUS_MASTER WHERE STATUS_ID=?";
    public static final String APP_STATUS_DETAILS_OF_MENTEE = "SELECT B.STATUS_DESCRIPTION, TO_CHAR(A.MENTEE_INITIATED_DATE,'DD-MON-YY'), TO_CHAR(A.APPROVAL_REJECTION_DATE,'DD-MON-YY'), A.COMMENTS,B.STATUS_ID,A.MENTOR_MENTEE_MAPPING_ID FROM MENTOR_MENTOR_MENTEE_MAPPING A, MENTOR_STATUS_MASTER B WHERE B.STATUS_ID = A.STATUS_ID AND SECURE_DATA.DECRYPTDATA(MENTEE_SSOID)=? AND SECURE_DATA.DECRYPTDATA(MENTOR_SSOID) = ? ORDER BY A.APPROVAL_REJECTION_DATE";
    public static final String APP_STATUS_ID_STATUS_QUERY = "SELECT STATUS_ID FROM MENTOR_REGISTERED_MENTEES WHERE SECURE_DATA.DECRYPTDATA(MENTEE_SSOID)=?";
    
    public static final String APP_MENTEE_REGISTERED_SLNO_QUERY = "SELECT MENTEE_REGISTERED_SLNO_SEQ.NEXTVAL FROM DUAL";
    public static final String APP_MENTEE_ROLES_INSERTION_QUERY = "INSERT INTO MENTOR_MENTEE_ROLS_INFO(ROLS_ID,REGISTERED_MENTEE_SLNO,FROM_DATE,TO_DATE,ORGANIZATION,DESIGNATION,LOCATION) VALUES(MENTEE_REGISTERED_ROLE_SLNO.NEXTVAL,?,TO_DATE(?,'MM/YYYY'),TO_DATE(?,'MM/YYYY'),?,?,?)";
    public static final String APP_MENTEE_ROLES_LIST_QUERY = "SELECT TO_CHAR(A.FROM_DATE,'MM') FROM_MONTH,TO_CHAR(A.FROM_DATE,'YYYY') FROM_YEAR,TO_CHAR(A.TO_DATE,'MM') TO_MONTH,TO_CHAR(A.TO_DATE,'YYYY') TO_YEAR,A.ORGANIZATION,A.DESIGNATION,A.LOCATION,A.FROM_DATE,A.TO_DATE FROM MENTOR_MENTEE_ROLS_INFO A,MENTOR_REGISTERED_MENTEES B WHERE A.REGISTERED_MENTEE_SLNO=B.MENTEE_REGISTERED_SLNO AND SECURE_DATA.DECRYPTDATA(MENTEE_SSOID)=?" ;
    public static  final String APP_EXISTING_MENTEE_REGISTERED_SLNO_QUERY = "SELECT MENTEE_REGISTERED_SLNO FROM MENTOR_REGISTERED_MENTEES WHERE SECURE_DATA.DECRYPTDATA(MENTEE_SSOID)=?";
    public static final String APP_EXIST_MENTEE_ROLES_DELETE = "DELETE FROM MENTOR_MENTEE_ROLS_INFO WHERE REGISTERED_MENTEE_SLNO=?";

    public static final String APP_REGMENTEE_DETAILS = "SELECT A.GEHRFULLNAME,A.GEBUSINESSUNIT,A.GEHRPOSITIONTITLE,TO_CHAR(B.REGISTRATION_DATE,'DD-MON-YY'),A.SSOID FROM MENTOR_SPODA_EMPLOYEE A, MENTOR_REGISTERED_MENTEES B WHERE A.LOCATIONID IN(SELECT LOCATIONID FROM MENTOR_SPODA_EMPLOYEE WHERE SSOID=?) AND A.SSOID=SECURE_DATA.DECRYPTDATA(B.MENTEE_SSOID)AND B.STATUS_ID=10";
    public static final String APP_BUSINESSTYPE_QUERY = "SELECT GEBUSINESSUNIT FROM MENTOR_SPODA_EMPLOYEE WHERE SSOID=?";
    public static final String APP_NAME_OF_THE_MENTOR_QUERY = "SELECT SECURE_DATA.DECRYPTDATA(MENTOR_SSOID) FROM MENTOR_MENTOR_MENTEE_MAPPING WHERE SECURE_DATA.DECRYPTDATA(MENTEE_SSOID)=? AND STATUS_ID IN (5,7,9)";
    public static final String APP_LIST_MENTEES_QUERY = "SELECT secure_data.DECRYPTDATA(A.MENTEE_SSOID) ,A.STATUS_ID,C.GEHRFULLNAME,C.GEBUSINESSUNIT,C.GEHRPOSITIONTITLE,GEHRDAILCOM FROM MENTOR_MENTOR_MENTEE_MAPPING A, MENTOR_REGISTERED_MENTORS B,MENTOR_SPODA_EMPLOYEE C WHERE A.MENTOR_SSOID = B.MENTOR_SSOID AND C.SSOID=secure_data.DECRYPTDATA(A.MENTEE_SSOID) AND A.MENTOR_MENTEE_MAPPING_ID= (SELECT MAX(MENTOR_MENTEE_MAPPING_ID) FROM MENTOR_MENTOR_MENTEE_MAPPING WHERE secure_data.DECRYPTDATA(MENTOR_SSOID)=secure_data.DECRYPTDATA(A.MENTOR_SSOID) AND secure_data.DECRYPTDATA(MENTEE_SSOID)=secure_data.DECRYPTDATA(A.MENTEE_SSOID)) AND B.MENTOR_REGISTERED_SLNO=? AND A.STATUS_ID IN(5,6,9)";
    }


